package samples2019;

public class startswithendswith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Starts with
		if("Sunny".startsWith("Sun"))
			System.out.println("Starts with Sun");
		if("Starfish".endsWith("fish"))
			System.out.println("Ends with Fish");

	}

}
